import { ElMessage } from 'element-plus';
import { ref, onMounted } from 'vue';
import axios from '@/utils/request';
import * as echarts from 'echarts';
export default {
  __name: 'RewardData',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    let lineChart;
    const lineContainer = ref(null);
    const userRecentCreateData = ref([]);
    const dates = ref([]); //存放近七天的日期，供折线图的X轴

    const getPastSevenDays = () => {
      let currentDate = new Date();
      for (let i = 0; i < 7; i++) {
        dates.value.unshift(`${String(currentDate.getMonth() + 1).padStart(2, '0')}-${String(currentDate.getDate()).padStart(2, '0')}`);
        currentDate.setDate(currentDate.getDate() - 1);
      }
    };

    //获取用户近7天文章打赏数据
    async function getSevenRewardData() {
      try {
        const response = await axios.get(`/articledata/reward`);
        if (response.data.code === 1) {
          userRecentCreateData.value = response.data.data;
          // ElMessage.success('获取用户近7天文章打赏数据成功');
        } else if (response.data.code === 0 && response.data.msg) {
          ElMessage.error(response.data.msg.toString());
        } else {
          console.error("获取用户近7天文章打赏数据失败");
          ElMessage.error("获取用户近7天文章打赏数据失败");
        }
      } catch (error) {
        console.error("获取用户近7天文章打赏数据失败", error);
        ElMessage.error("获取用户近7天文章打赏数据失败");
      }
    }
    onMounted(async () => {
      getPastSevenDays();
      await getSevenRewardData();

      // 折线图配置
      const lineOption = {
        title: {
          text: '近7天打赏数'
        },
        tooltip: {},
        grid: {
          height: "70%"
        },
        xAxis: {
          type: 'category',
          data: dates.value
        },
        yAxis: {
          type: 'value',
          splitNumber: 4
        },
        series: [{
          data: userRecentCreateData.value,
          type: 'line',
          smooth: true,
          barWidth: 60
        }]
      };

      // 初始化图表
      lineChart = echarts.init(lineContainer.value);
      lineChart.setOption(lineOption);
    });
    const __returned__ = {
      get lineChart() {
        return lineChart;
      },
      set lineChart(v) {
        lineChart = v;
      },
      lineContainer,
      userRecentCreateData,
      dates,
      getPastSevenDays,
      getSevenRewardData,
      get ElMessage() {
        return ElMessage;
      },
      ref,
      onMounted,
      get axios() {
        return axios;
      },
      get echarts() {
        return echarts;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
